function T = T1_conf1_V4(rho,c,vw1,vw2,inv,x01,x02,x03,x04,x05,x06)
 T = [(1/2).*(vw1.*(2.*vw2.*(x01+(-1).*x03)+(-2).*vw1.*(x02+(-1).*x04)) ...
  .^2.*((4.*vw1.^2+4.*vw2.^2).*((x01+(-1).*x03).^2+(x02+(-1).*x04) ...
  .^2)).^(-1/2)+(-1/12).*c.*inv.*(4.*vw1.^2+4.*vw2.^2).*((-1).*x02+ ...
  x04).*((-1/4).*pi.^2+(pi+(-1).*acos(inv.*(2.*vw2.*(x01+(-1).*x03)+ ...
  (-2).*vw1.*(x02+(-1).*x04)).*((4.*vw1.^2+4.*vw2.^2).*((x01+(-1).* ...
  x03).^2+(x02+(-1).*x04).^2)).^(-1/2))).^2)),(1/2).*(vw2.*(2.*vw2.* ...
  (x01+(-1).*x03)+(-2).*vw1.*(x02+(-1).*x04)).^2.*((4.*vw1.^2+4.* ...
  vw2.^2).*((x01+(-1).*x03).^2+(x02+(-1).*x04).^2)).^(-1/2)+(-1/12) ...
  .*c.*inv.*(4.*vw1.^2+4.*vw2.^2).*(x01+(-1).*x03).*((-1/4).*pi.^2+( ...
  pi+(-1).*acos(inv.*(2.*vw2.*(x01+(-1).*x03)+(-2).*vw1.*(x02+(-1).* ...
  x04)).*((4.*vw1.^2+4.*vw2.^2).*((x01+(-1).*x03).^2+(x02+(-1).*x04) ...
  .^2)).^(-1/2))).^2)),0,0,0,0];

 T(3)=T(1);
 T(4)=T(2);
 T=0.5*rho*T;
end