T = -[(1/3).*rho.*((x03+(-1).*x05).^2+(x04+(-1).*x06).^2).^(-1/2).*( ...
  x04+(-1).*x06).^2,0,(1/3).*rho.*((x03+(-1).*x05).^2+(x04+(-1).* ...
  x06).^2).^(-1/2).*(x04+(-1).*x06).^2,0,(1/3).*rho.*((x03+(-1).* ...
  x05).^2+(x04+(-1).*x06).^2).^(-1/2).*(x04+(-1).*x06).^2,0].';

end